import * as THREE from "three";
import { GLTFLoader } from "three/examples/jsm/loaders/GLTFLoader";
export default {
  name: "ThreeModelViewer",
  data() {
    return {
      threejs: null,
      scene: null,
      camera: null,
      mixers: [],
      headAction: [0],
      neckBone: null,
      mouse: new THREE.Vector2(),
      rafId: null
    };
  },
  methods: {
    initialize() {
      // Create renderer
      this.threejs = new THREE.WebGLRenderer({
        antialias: true
      });
      this.threejs.shadowMap.enabled = true;
      this.threejs.shadowMap.type = THREE.PCFSoftShadowMap;
      this.threejs.setPixelRatio(window.devicePixelRatio);
      this.threejs.setSize(128, 128);

      // Attach renderer to DOM
      this.$refs.threeContainer.appendChild(this.threejs.domElement);

      // Camera setup
      const fov = 50;
      const aspect = 1;
      const near = 1;
      const far = 1000.0;
      this.camera = new THREE.PerspectiveCamera(fov, aspect, near, far);
      this.camera.position.set(0, 0.5, 2.3);
      this.camera.lookAt(new THREE.Vector3(0, 0.6, 0));

      // Scene setup
      this.scene = new THREE.Scene();

      // Lights
      const directionalLight = new THREE.DirectionalLight(0xffffff, 4.0);
      directionalLight.position.set(5, 10, 7.5);
      directionalLight.castShadow = true;
      this.scene.add(directionalLight);
      const ambientLight = new THREE.AmbientLight(0x846e62, 5.0);
      this.scene.add(ambientLight);

      // Load model
      this.loadModel();

      // Mouse move listener
      this.threejs.domElement.addEventListener("mousemove", this.onMouseMove, false);

      // Start render loop
      this.raf();
    },
    loadModel() {
      const loader = new GLTFLoader();
      console.log(loader);
      loader.load("/lico-actions-subd.glb", gltf => {
        gltf.scene.traverse(c => {
          if (c.material) {
            c.material.side = THREE.FrontSide;
            c.material.depthWrite = true;
          }
          if (c.name && c.name == "floor") {
            c.material.color.set(0x00ff00);
          }
          c.castShadow = c.name !== "floor";
        });
        const idsToRemove = [];
        gltf.animations[0].tracks.forEach((t, i) => {
          if (t.name.includes("mixamorigHead.quaternion")) {
            idsToRemove.push(i);
          }
        });
        idsToRemove.forEach(i => {
          gltf.animations[0].tracks.splice(i, 1);
        });

        // const mixer = new THREE.AnimationMixer(gltf.scene);
        // this.mixers.push(mixer);
        // gltf.animations.forEach((element, i) => {
        //   this.action[i] = mixer.clipAction(element);
        // });
        // this.action[0].play();
        // this.playing = 0;

        this.scene.add(gltf.scene);
        this.neckBone = gltf.scene.getObjectByName("mixamorigHead");
        if (gltf.animations.length > 0) {
          const mixer = new THREE.AnimationMixer(gltf.scene);
          const action = mixer.clipAction(gltf.animations[0]);
          action.play();

          // Update the mixer on every frame
          this.mixers = [mixer];
        }
      });
    },
    onMouseMove(event) {
      // if (this.headAction.includes(this.playing)) {
      this.mouse.set(event.clientX / event.currentTarget.clientWidth * 2 - 1, -(event.clientY / event.currentTarget.clientHeight) * 2 + 1);
      if (this.neckBone) {
        this.neckBone.lookAt(this.mouse.x / 2, (this.mouse.y + 0.8) / 2, 1);
      }
      // }
    },
    raf() {
      this.rafId = requestAnimationFrame(t => {
        if (this.previousRAF === null) {
          this.previousRAF = t;
        }
        this.raf();
        const timeElapsed = t - this.previousRAF;
        this.previousRAF = t;
        this._Step(timeElapsed);
        this.threejs.render(this.scene, this.camera);
      });
    },
    _Step(timeElapsed) {
      const delta = timeElapsed * 0.001;
      if (this.mixers.length > 0) {
        this.mixers[0].update(delta);
      }
    },
    cleanup() {
      cancelAnimationFrame(this.rafId);
      this.threejs.domElement.removeEventListener("mousemove", this.onMouseMove);
      this.threejs.dispose();
      this.scene = null;
      this.camera = null;
      this.mixers = [];
    }
  },
  mounted() {
    this.initialize();
  },
  beforeUnmount() {
    this.cleanup();
  }
};
